# Define the two binary strings
bin_str1 = ""
bin_str2 = ""

# Compute the Hamming distance
hamming_distance = sum(c1 != c2 for c1, c2 in zip(bin_str1, bin_str2))
print(
    "dist:",
    hamming_distance,
    "len_eq:",
    len(bin_str1) == len(bin_str2),
    "error:",
    hamming_distance / len(bin_str1) * 100,
)
